begin
wwv_flow_imp_shared.create_authentication(
 p_id=>wwv_flow_imp.id(3720948540686283937)
,p_name=>'Database Accounts'
,p_scheme_type=>'NATIVE_CUSTOM'
,p_attribute_03=>'user_auth'
,p_attribute_05=>'N'
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'FUNCTION user_auth( ',
'    p_username IN VARCHAR2, --User_Name ',
'    p_password IN VARCHAR2 -- Password ',
')',
'    RETURN BOOLEAN ',
'AS ',
'    lc_pwd_exit VARCHAR2(1);',
'BEGIN ',
'-- Validate whether the user exits or not ',
'    SELECT ''Y'' ',
'    INTO lc_pwd_exit ',
'    FROM APPLICTION_USER ',
'    WHERE upper(email) = UPPER(p_username) ',
'    AND password = p_password',
'; ',
'RETURN TRUE; ',
'EXCEPTION ',
