,p_process_type=>'NATIVE_FORM_DML'
,p_process_name=>'Process form Property_forom'
,p_attribute_01=>'PLSQL_CODE'
,p_attribute_04=>wwv_flow_string.join(wwv_flow_t_varchar2(
'BEGIN',
'  IF :REQUEST = ''CREATE'' THEN',
'    -- Insert operation',
'    -- Get the next available LOCATION_ID and assign it to l_location_id',
'    SELECT LOCATION_ID_SEQ.NEXTVAL INTO :P19_LOCATION_ID FROM DUAL;',
'    -- Get the next available PROPERTY_ID and assign it to l_property_id',
'    SELECT PROPERTY_ID_SEQ.NEXTVAL INTO :P19_PROPERTY_ID FROM DUAL;',
'',
'    -- Insert into LOCATION table',
'    INSERT INTO LOCATION (LOCATION_ID, COUNTRY, CITY, STREET, HOUSE_NUMBER, APARTMENT_NUMBER, FLOOR,',
'      PROPERTY_PROPERTY_ID)',
'    VALUES (:P19_LOCATION_ID, :P19_COUNTRY, :P19_CITY, :P19_STREET, :P19_HOUSE_NUMBER, :P19_APARTMENT_NUMBER,',
'      :P19_FLOOR, NULL);',
'',
'    -- Insert into PROPERTY table',
'    INSERT INTO PROPERTY (PROPERTY_ID, PROPERTY_NAME, DESCRIPTION, PRICE, PROPERTY_TYPE, HAS_TV,',
'      HAS_KITCHEN, HAS_AIR_CON, HOST_USER_ID, IMAGE_URL, LOCATION_LOCATION_ID)',
'    VALUES (:P19_PROPERTY_ID, :P19_PROPERTY_NAME, :P19_DESCRIPTION, :P19_PRICE, :P19_PROPERTY_TYPE, :P19_HAS_TV,',
'      :P19_HAS_KITCHEN, :P19_HAS_AIR_CON, :P19_HOST_USER_ID, :P19_IMAGE_URL, :P19_LOCATION_ID);',
'',
'    -- Update LOCATION table',
'    UPDATE LOCATION',
'    SET PROPERTY_PROPERTY_ID = :P19_PROPERTY_ID',
'    WHERE LOCATION_ID = :P19_LOCATION_ID;',
'',
'  ELSIF :REQUEST = ''SAVE'' THEN',
'    -- Update operation',
'    -- Update LOCATION table',
